import React, { Component } from 'react'
import { Button, Card, CardImg, Col, Form, FormGroup, Input, Label, Modal, ModalBody, ModalFooter, ModalHeader, Row } from 'reactstrap';
import Page from 'components/Page';
import { getImages, PostImages } from '../host/config';


export default class FormPage extends Component {



  state={
    file:[],
    data:[],
    data1:[],
    dataA:[],
    modal:false

    }


    postImage=()=>{
      var data={
        "image": this.state.file
      }
      PostImages(data).then(res=>{
        this.postImage()
      })
      this.toggle1()
    }

    getImage=()=>{
      getImages().then(res=>{
    this.setState({data1:res.dataA})
    console.log(res.dataA);
      })  
      console.log(this.state.data1);
    }


    toggle =() => {
      this.setState({modal:true})
    };
    toggle1 = () => {
      this.setState({modal:false})
    };
    handleFile(e) {
      let file1 = e.target.files[0];
    console.log(file1);
    this.setState({file:file1})


    }
    componentDidMount(){
      setTimeout(() => {
          // this.getCategory()
          this.getImage()
      }, 100);

    }

  render() {


    return (
      <Page title="Images" breadcrumbs={[{ name: 'Images', active: true }]}>
                <Button color="primary" onClick={this.toggle}>create</Button>

          <Row>
            {this.state.data1.map(item=>{
return <Col lg={6} md={12} sm={12} xs={12} className="mb-3">
          <Card className="flex-row">
            <CardImg
              className="card-img-left"
              src={item.imageURL}
              style={{ width: 'auto', height: 150 }}
            />
          </Card>
        </Col>})}</Row>


        <Modal isOpen={this.state.modal}>
                  <ModalHeader >Modal title</ModalHeader>
                  <ModalBody>
                  <Form>
                <FormGroup>
                  <Label for="exampleImage">Image</Label>
                  <Input onInput={(e)=>this.handleFile(e)} id="file" type="file" >
                  </Input>
                </FormGroup>
                </Form>
                  </ModalBody>
                  <ModalFooter>
                    <Button color="primary" onClick={this.postImage}>
                     save
                    </Button>
                    <Button color="secondary" onClick={this.toggle1}>
                      Cancel
                    </Button>
                  </ModalFooter>
                </Modal>
    </Page>
    )
  }
}
