import logo200Image from 'assets/img/logo/logo_200.png';
import React from 'react';
import { Button, Form, FormGroup, Input, Label } from 'reactstrap';

class AuthForm extends React.Component {


  changeAuthState = event => {
    event.preventDefault();
console.log(event);
  };

  handleSubmit = event => {
    event.preventDefault();
  console.log(event);
  };

  render() {

    return (<div style={{width:'100vw',height:'100vh',display:'flex', justifyContent:'center',alignItems:'center' }}>

      <Form onSubmit={this.handleSubmit} style={{width:'300px'}}>

          <div className="text-center pb-4">
            <img
              src={logo200Image}
              className="rounded"
              style={{ width: 60, height: 60, cursor: 'pointer' }}
              alt="logo"
            />
          </div>

        <FormGroup>
          <Label for="login">User Name</Label>
          <Input placeholder="your Login" type="text" />
        </FormGroup>
        <FormGroup>
          <Label for='Password'>Password</Label>
          <Input type="password" id="Password"  placeholder='your password' />
        </FormGroup>


        <hr />
        <Button
          size="lg"
          className="bg-gradient-theme-left border-0"
          block
          onClick={this.handleSubmit}>
          Login
        </Button>

      </Form> </div> 
    );
  }
}


export default AuthForm;
