import Page from 'components/Page';
import React,{Component} from 'react';
import { Button, Card, CardBody, CardImg, CardText, CardTitle, Col, Modal, ModalBody, ModalFooter, ModalHeader, Row } from 'reactstrap';
// import { Button, ButtonGroup, ButtonToolbar, Card, CardBody, CardHeader, CardText, Col, DropdownItem, DropdownMenu, DropdownToggle, Row, UncontrolledButtonDropdown } from 'reactstrap';
import bg1Image from 'assets/img/bg/background_640-1.jpg';


export default class ButtonGroupPage extends Component {
  state = {
    modal: false,
  };

  toggle =() => {
    // if (!modalType) {
    //   return this.setState({
    //     modal: !this.state.modal,
    //   });
    // }

    // this.setState({
    //   [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    // });
    var a=!this.state.modal
    this.setState({modal:a})
  };
  render() {
    return (
    <Page
      title="Category"
      breadcrumbs={[{ name: 'Category', active: true }]}
    >
                <Button color="primary" onClick={()=>this.toggle()}>primary</Button>

          <Row>
        <Col md={6} sm={6} xs={12} className="mb-3">
          <Card className="flex-row">
            <CardImg
              className="card-img-left"
              src={bg1Image}
              style={{ width: 'auto', height: 150 }}
            />
            <CardBody>
              <CardTitle>Horizontal Image Card(Left)</CardTitle>
              <CardText>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </CardText>
              <Button color="success">success</Button>
              <Button color="secondary ml-2">secondary</Button>
            </CardBody>
          </Card>
        </Col></Row>
        <Modal
                  isOpen={this.state.modal}
                  toggle={this.toggle()}
                  className={this.props.className}>
                  <ModalHeader toggle={this.toggle()}>Modal title</ModalHeader>
                  <ModalBody>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                    sed do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                    ullamco laboris nisi ut aliquip ex ea commodo consequat.
                    Duis aute irure dolor in reprehenderit in voluptate velit
                    esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
                    occaecat cupidatat non proident, sunt in culpa qui officia
                    deserunt mollit anim id est laborum.
                  </ModalBody>
                  <ModalFooter>
                    <Button color="primary" onClick={this.toggle()}>
                      Do Something
                    </Button>{' '}
                    <Button color="secondary" onClick={this.toggle()}>
                      Cancel
                    </Button>
                  </ModalFooter>
                </Modal>
    </Page>
    )
  }
}
